-- Table: public.usuariosCREATE TABLE public.usuarios (    id bigint NOT NULL,    email character varying(255) COLLATE pg_catalog."default",    nome character varying(255) COLLATE pg_catalog."default" NOT NULL,    CONSTRAINT pk_usuarios PRIMARY KEY (id))TABLESPACE pg_default;ALTER TABLE public.usuarios OWNER to postgres;COMMENT ON COLUMN public.usuarios.id IS 'Identificador do registro';COMMENT ON COLUMN public.usuarios.email IS 'Email do cliente';COMMENT ON COLUMN public.usuarios.nome IS 'Nome do cliente';-- Table: public.plantas_familiaCREATE TABLE public.plantas_familia (    id bigint NOT NULL,    descricao character varying(255) COLLATE pg_catalog."default" NOT NULL,    CONSTRAINT pk_plantas_familia PRIMARY KEY (id))TABLESPACE pg_default;ALTER TABLE public.plantas_familia OWNER to postgres;COMMENT ON COLUMN public.plantas_familia.id IS 'Identificador do registro';COMMENT ON COLUMN public.plantas_familia.descricao IS 'Descricao da familia';-- Table: public.plantas_utilizacaoCREATE TABLE public.plantas_utilizacao (    id bigint NOT NULL,    descricao character varying(255) COLLATE pg_catalog."default" NOT NULL,    observacao character varying(500) COLLATE pg_catalog."default",    CONSTRAINT pk_plantas_utilizacao PRIMARY KEY (id))TABLESPACE pg_default;ALTER TABLE public.plantas_utilizacao OWNER to postgres;COMMENT ON COLUMN public.plantas_utilizacao.id IS 'Identificador do registro';COMMENT ON COLUMN public.plantas_utilizacao.descricao IS 'Descricao da utilizacao';COMMENT ON COLUMN public.plantas_utilizacao.observacao IS 'Observacao';-- Table: public.plantasCREATE TABLE public.plantas (    id bigint NOT NULL,    id_planta_familia bigint NOT NULL,    id_planta_utilizacao bigint,    nome_comum character varying(255) COLLATE pg_catalog."default" NOT NULL,    nome_cientifico character varying(255) COLLATE pg_catalog."default" NOT NULL,    dt_coleta date NOT NULL,    local_coleta character varying(255) COLLATE pg_catalog."default",    observacao character varying(500) COLLATE pg_catalog."default",    CONSTRAINT pk_plantas PRIMARY KEY (id),    CONSTRAINT fk_planta_familia_planta FOREIGN KEY (id_planta_familia)        REFERENCES public.plantas_familia (id) MATCH SIMPLE        ON UPDATE NO ACTION        ON DELETE NO ACTION,    CONSTRAINT fk_planta_utilizacao_planta FOREIGN KEY (id_planta_utilizacao)        REFERENCES public.plantas_utilizacao (id) MATCH SIMPLE        ON UPDATE NO ACTION        ON DELETE NO ACTION)TABLESPACE pg_default;ALTER TABLE public.plantas OWNER to postgres;COMMENT ON COLUMN public.plantas.id IS 'Identificador do registro';COMMENT ON COLUMN public.plantas.nome_comum IS 'Nome comum da produto';COMMENT ON COLUMN public.plantas.nome_cientifico IS 'Nome cientifico da produto';COMMENT ON COLUMN public.plantas.dt_coleta IS 'Data da coleta/cadastro';COMMENT ON COLUMN public.plantas.local_coleta IS 'Local de coleta';COMMENT ON COLUMN public.plantas.observacao IS 'Observacao';COMMENT ON COLUMN public.plantas.id_planta_familia IS 'Identificador da familia da produto';COMMENT ON COLUMN public.plantas.id_planta_utilizacao IS 'Identificador da utilizacao da produto';-- SequencesCREATE SEQUENCE public.seq_usuarios    INCREMENT 1    START 2    MINVALUE 1    MAXVALUE 99999999999999999    CACHE 1;ALTER SEQUENCE public.seq_usuarios OWNER TO postgres;CREATE SEQUENCE public.seq_plantas_familia    INCREMENT 1    START 2    MINVALUE 1    MAXVALUE 99999999999999999    CACHE 1;ALTER SEQUENCE public.seq_plantas_familia OWNER TO postgres;CREATE SEQUENCE public.seq_plantas_utilizacao    INCREMENT 1    START 2    MINVALUE 1    MAXVALUE 99999999999999999    CACHE 1;ALTER SEQUENCE public.seq_plantas_utilizacao OWNER TO postgres;CREATE SEQUENCE public.seq_plantas    INCREMENT 1    START 2    MINVALUE 1    MAXVALUE 99999999999999999    CACHE 1;ALTER SEQUENCE public.seq_plantas OWNER TO postgres;