package br.vandersoncamp.simples.model;import javax.persistence.*;import javax.validation.constraints.NotNull;import java.io.Serializable;import java.math.BigDecimal;@Entity@Table(name = "entrada_itens")@SequenceGenerator(name = "seq_entrada_itens", sequenceName = "seq_entrada_itens", initialValue = 1, allocationSize = 1)public class EntradaItem implements Serializable, EntityID {    private static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_entrada_itens")    private Long id;    @NotNull(message = "Entrada não pode ser nula")    @ManyToOne    @JoinColumn(name = "i_entradas", nullable = false)    private Entrada entrada;    @NotNull(message = "Produto não pode ser nulo")    @ManyToOne    @JoinColumn(name = "i_produtos", nullable = false)    private Produto produto;    @NotNull(message = "Fornecedor não pode ser nulo")    @ManyToOne    @JoinColumn(name = "i_fornecedores", nullable = false)    private Fornecedor fornecedor;    @Column(name = "nro_item", nullable = true)    private Long numeroItem;    @Column(name = "qtd_item", nullable = true)    private BigDecimal quantidade;    @Column(name = "valor_compra", nullable = true)    private BigDecimal valorCompra;    @Column(name = "valor_total", nullable = true)    private BigDecimal valorTotal;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public Entrada getEntrada() {        return entrada;    }    public void setEntrada(Entrada entrada) {        this.entrada = entrada;    }    public Produto getProduto() {        return produto;    }    public void setProduto(Produto produto) {        this.produto = produto;    }    public Fornecedor getFornecedor() {        return fornecedor;    }    public void setFornecedor(Fornecedor fornecedor) {        this.fornecedor = fornecedor;    }    public BigDecimal getValorCompra() {        return valorCompra;    }    public void setValorCompra(BigDecimal valorCompra) {        this.valorCompra = valorCompra;    }    public Long getNumeroItem() {        return numeroItem;    }    public void setNumeroItem(Long numeroItem) {        this.numeroItem = numeroItem;    }    public BigDecimal getQuantidade() {        return quantidade;    }    public void setQuantidade(BigDecimal quantidade) {        this.quantidade = quantidade;    }    public BigDecimal getValorTotal() {        return valorTotal;    }    public void setValorTotal(BigDecimal valorTotal) {        this.valorTotal = valorTotal;    }}