package br.vandersoncamp.simples.model;import javax.persistence.*;import javax.validation.constraints.NotNull;import java.io.Serializable;import java.math.BigDecimal;import java.time.LocalDate;@Entity@Table(name = "movimentos")@SequenceGenerator(name = "seq_movimentos", sequenceName = "seq_movimentos", initialValue = 1, allocationSize = 1)public class MovimentoEstoque implements Serializable, EntityID {    private static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_movimentos")    private Long id;    @NotNull    @Column(name = "tipo", length = 1, nullable = false)    private String tipo;    @NotNull    @Column(name = "data_movimento", nullable = false)    private LocalDate dataMovimento;    @ManyToOne    @JoinColumn(name = "i_entradas", nullable = false)    private Entrada entrada;    @ManyToOne    @JoinColumn(name = "i_entrada_itens", nullable = false)    private EntradaItem entradaItem;    @ManyToOne    @JoinColumn(name = "i_saidas", nullable = false)    private Saida saida;    @ManyToOne    @JoinColumn(name = "i_saidas_itens", nullable = false)    private SaidaItem saidaItem;    @Column(name = "quantidade", nullable = true)    private BigDecimal quantidade;    @Column(name = "valor_unitario", nullable = true)    private BigDecimal valorUnitario;    @Column(name = "valor_total", nullable = true)    private BigDecimal valorTotal;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getTipo() {        return tipo;    }    public void setTipo(String tipo) {        this.tipo = tipo;    }    public Entrada getEntrada() {        return entrada;    }    public void setEntrada(Entrada entrada) {        this.entrada = entrada;    }    public EntradaItem getEntradaItem() {        return entradaItem;    }    public void setEntradaItem(EntradaItem entradaItem) {        this.entradaItem = entradaItem;    }    public Saida getSaida() {        return saida;    }    public void setSaida(Saida saida) {        this.saida = saida;    }    public SaidaItem getSaidaItem() {        return saidaItem;    }    public void setSaidaItem(SaidaItem saidaItem) {        this.saidaItem = saidaItem;    }    public BigDecimal getQuantidade() {        return quantidade;    }    public void setQuantidade(BigDecimal quantidade) {        this.quantidade = quantidade;    }    public BigDecimal getValorUnitario() {        return valorUnitario;    }    public void setValorUnitario(BigDecimal valorUnitario) {        this.valorUnitario = valorUnitario;    }    public BigDecimal getValorTotal() {        return valorTotal;    }    public void setValorTotal(BigDecimal valorTotal) {        this.valorTotal = valorTotal;    }}