package br.vandersoncamp.simples.model;import javax.persistence.*;import javax.validation.constraints.NotNull;import java.io.Serializable;import java.time.LocalDate;@Entity@Table(name = "saidas")@SequenceGenerator(name = "seq_saidas", sequenceName = "seq_saidas", initialValue = 1, allocationSize = 1)public class Saida implements Serializable, EntityID {    private static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_saidas")    private Long id;    @NotNull(message = "Não foi informado o tipo de saída")    @Column(name = "tipo_saida", nullable = false)    private String tipoSaida;    @NotNull(message = "Não foi informada a data de saída")    @Column(name = "data_saida", nullable = false)    private LocalDate dataSaida;    @NotNull(message = "Não foi informado o cliente")    @ManyToOne    @JoinColumn(name = "i_clientes", nullable = false)    private Cliente cliente;    @Column(name = "observacao", length = 500, nullable = true)    private String observacao;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public Cliente getCliente() {        return cliente;    }    public void setCliente(Cliente cliente) {        this.cliente = cliente;    }    public String getObservacao() {        return observacao;    }    public void setObservacao(String observacao) {        this.observacao = observacao;    }    public String getTipoSaida() {        return tipoSaida;    }    public void setTipoSaida(String tipoSaida) {        this.tipoSaida = tipoSaida;    }    public LocalDate getDataSaida() {        return dataSaida;    }    public void setDataSaida(LocalDate dataSaida) {        this.dataSaida = dataSaida;    }}