package br.vandersoncamp.simples.model;import javax.persistence.*;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;import java.io.Serializable;@Entity@Table(name = "saidas")@SequenceGenerator(name = "seq_saidas", sequenceName = "seq_saidas", initialValue = 1, allocationSize = 1)public class Saida implements Serializable, EntityID {    private static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_saidas")    private Long id;    @NotNull    @Column(name = "tipo_saida", nullable = false)    private String tipoSaida;    @NotNull    @ManyToOne    @JoinColumn(name = "i_clientes", nullable = false)    private Cliente cliente;    @Size(max = 500)    @Column(name = "observacao", length = 500, nullable = true)    private String observacao;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public Cliente getCliente() {        return cliente;    }    public void setCliente(Cliente cliente) {        this.cliente = cliente;    }    public String getObservacao() {        return observacao;    }    public void setObservacao(String observacao) {        this.observacao = observacao;    }    public String getTipoSaida() {        return tipoSaida;    }    public void setTipoSaida(String tipoSaida) {        this.tipoSaida = tipoSaida;    }}