package br.vandersoncamp.simples.model;import javax.persistence.*;import javax.validation.constraints.NotNull;import java.io.Serializable;import java.math.BigDecimal;import java.time.LocalDate;@Entity@Table(name = "saldos")@SequenceGenerator(name = "seq_saldos", sequenceName = "seq_saldos", initialValue = 1, allocationSize = 1)public class SaldoEstoque implements Serializable, EntityID {    private static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_saldos")    private Long id;    @NotNull(message = "Produto não pode ser nulo")    @ManyToOne    @JoinColumn(name = "i_produtos", nullable = false)    private Produto produto;    @NotNull(message = "Data do saldo não pode ser nula")    @Column(name = "data_saldo", nullable = false)    private LocalDate dataSaldo;    @Column(name = "quantidade", nullable = true)    private BigDecimal quantidade;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public Produto getProduto() {        return produto;    }    public void setProduto(Produto produto) {        this.produto = produto;    }    public LocalDate getDataSaldo() {        return dataSaldo;    }    public void setDataSaldo(LocalDate dataSaldo) {        this.dataSaldo = dataSaldo;    }    public BigDecimal getQuantidade() {        return quantidade;    }    public void setQuantidade(BigDecimal quantidade) {        this.quantidade = quantidade;    }}