package br.vandersoncamp.simples.model;import javax.persistence.*;import javax.validation.constraints.NotNull;import java.io.Serializable;import java.math.BigDecimal;@Entity@Table(name = "saldo_estoque")@SequenceGenerator(name = "seq_salds_estoques", sequenceName = "seq_salds_estoques", initialValue = 1, allocationSize = 1)public class SaldoEstoque implements Serializable, EntityID {    private static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_salds_estoques")    private Long id;    @NotNull    @Column(name = "tipo_movimento",length = 1, nullable = false)    private String tipoMovimento;    @ManyToOne    @JoinColumn(name = "i_entradas", nullable = false)    private Entrada entrada;    @ManyToOne    @JoinColumn(name = "i_entrada_itens", nullable = false)    private EntradaItem entradaItem;    @ManyToOne    @JoinColumn(name = "i_vendas", nullable = false)    private Venda venda;    @ManyToOne    @JoinColumn(name = "i_vendas_itens", nullable = false)    private VendaItem vendaItem;    @Column(name = "quantidade", nullable = true)    private BigDecimal quantidade;    @Column(name = "valor_unitario", nullable = true)    private BigDecimal valorUnitario;    @Column(name = "valor_total", nullable = true)    private BigDecimal valorTotal;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getTipoMovimento() {        return tipoMovimento;    }    public void setTipoMovimento(String tipoMovimento) {        this.tipoMovimento = tipoMovimento;    }    public Entrada getEntrada() {        return entrada;    }    public void setEntrada(Entrada entrada) {        this.entrada = entrada;    }    public EntradaItem getEntradaItem() {        return entradaItem;    }    public void setEntradaItem(EntradaItem entradaItem) {        this.entradaItem = entradaItem;    }    public Venda getVenda() {        return venda;    }    public void setVenda(Venda venda) {        this.venda = venda;    }    public VendaItem getVendaItem() {        return vendaItem;    }    public void setVendaItem(VendaItem vendaItem) {        this.vendaItem = vendaItem;    }    public BigDecimal getQuantidade() {        return quantidade;    }    public void setQuantidade(BigDecimal quantidade) {        this.quantidade = quantidade;    }    public BigDecimal getValorUnitario() {        return valorUnitario;    }    public void setValorUnitario(BigDecimal valorUnitario) {        this.valorUnitario = valorUnitario;    }    public BigDecimal getValorTotal() {        return valorTotal;    }    public void setValorTotal(BigDecimal valorTotal) {        this.valorTotal = valorTotal;    }}