package br.vandersoncamp.simples.util;import javax.validation.*;import java.util.HashSet;import java.util.Set;public class BeanValidation {    private BeanValidation() {    }    public static void validate(Object object){        Set<ConstraintViolation<?>> violations = new HashSet<>();        ValidatorFactory dfv = Validation.buildDefaultValidatorFactory();        Validator validator = dfv.getValidator();        try {            violations.addAll(validator.validate(object));        } catch (UnexpectedTypeException cause) {        }        if(!violations.isEmpty()){            System.err.println("\n\n\nErros de Validação: ");        }        for (ConstraintViolation<?> violation : violations) {            System.err.println(violation.getMessage());        }    }}