package br.vandersoncamp.resources;import br.vandersoncamp.simples.business.EntradaBusiness;import br.vandersoncamp.simples.model.Entrada;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("/entradas")@RequestScopedpublic class EntradaResource {    @Inject    private EntradaBusiness business;    @GET    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        Entrada entrada = business.buscar(id);        if (entrada == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(entrada).build();    }    @GET    @Path("all")    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar(            @QueryParam("filterField") String filterField,            @QueryParam("filterValue") String filterValue,            @QueryParam("order") String order) {        return Response.ok(business.pesquisar(filterField, filterValue, order)).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(Entrada entrada) {        business.criar(entrada);        return Response.status(Response.Status.CREATED).entity(entrada).build();    }    @PUT    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response atualizar(Entrada entrada, @PathParam("id") Long id) {        if (!id.equals(entrada.getId())) {            return Response.status(Response.Status.BAD_REQUEST).entity("ID do objeto difere do ID da URL").build();        }        business.atualizar(entrada);        return Response.status(Response.Status.OK).entity(entrada).build();    }    @DELETE    @Path("{id}")    public Response excluir(@PathParam("id") Long id) {        business.excluir(id);        return Response.status(Response.Status.NO_CONTENT).build();    }}