package br.vandersoncamp.resources;import br.vandersoncamp.simples.business.FornecedorBusiness;import br.vandersoncamp.simples.model.Fornecedor;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("/fornecedores")@RequestScopedpublic class FornecedorResource {    @Inject    private FornecedorBusiness business;    @GET    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        Fornecedor fornecedor = business.buscar(id);        if (fornecedor == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(fornecedor).build();    }    @GET    @Path("all")    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar(            @QueryParam("filterField") String filterField,            @QueryParam("filterValue") String filterValue,            @QueryParam("order") String order) {        return Response.ok(business.pesquisar(filterField, filterValue, order)).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(Fornecedor fornecedor) {        business.criar(fornecedor);        return Response.status(Response.Status.CREATED).entity(fornecedor).build();    }    @PUT    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response atualizar(Fornecedor fornecedor, @PathParam("id") Long id) {        if (!id.equals(fornecedor.getId())) {            return Response.status(Response.Status.BAD_REQUEST).entity("ID do objeto difere do ID da URL").build();        }        business.atualizar(fornecedor);        return Response.status(Response.Status.OK).entity(fornecedor).build();    }    @DELETE    @Path("{id}")    public Response excluir(@PathParam("id") Long id) {        business.excluir(id);        return Response.status(Response.Status.NO_CONTENT).build();    }}