package br.vandersoncamp.resources;import br.vandersoncamp.simples.business.produto.ProdutoBusiness;import br.vandersoncamp.simples.model.Produto;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("/produtos")@RequestScopedpublic class ProdutoResource {    @Inject    private ProdutoBusiness business;    @GET    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        Produto produto = business.buscar(id);        if (produto == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(produto).build();    }    @GET    @Path("all")    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar(            @QueryParam("filterField") String filterField,            @QueryParam("filterValue") String filterValue,            @QueryParam("order") String order) {        return Response.ok(business.pesquisar(filterField, filterValue, order)).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(Produto produto) {        business.criar(produto);        return Response.status(Response.Status.CREATED).entity(produto).build();    }    @PUT    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response atualizar(Produto produto, @PathParam("id") Long id) {        if (!id.equals(produto.getId())) {            return Response.status(Response.Status.BAD_REQUEST).entity("ID do objeto difere do ID da URL").build();        }        business.atualizar(produto);        return Response.status(Response.Status.OK).entity(produto).build();    }    @DELETE    @Path("{id}")    public Response excluir(@PathParam("id") Long id) {        business.excluir(id);        return Response.status(Response.Status.NO_CONTENT).build();    }}