package br.vandersoncamp.resources;import br.vandersoncamp.simples.business.SaidaItemBusiness;import br.vandersoncamp.simples.model.SaidaItem;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("/vendaitens")@RequestScopedpublic class SaidaItemResource {    @Inject    private SaidaItemBusiness business;    @GET    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        SaidaItem saidaItem = business.buscar(id);        if (saidaItem == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(saidaItem).build();    }    @GET    @Path("all")    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar(            @QueryParam("filterField") String filterField,            @QueryParam("filterValue") String filterValue,            @QueryParam("order") String order) {        return Response.ok(business.pesquisar(filterField, filterValue, order)).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(SaidaItem saidaItem) {        business.criar(saidaItem);        return Response.status(Response.Status.CREATED).entity(saidaItem).build();    }    @PUT    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response atualizar(SaidaItem saidaItem, @PathParam("id") Long id) {        if (!id.equals(saidaItem.getId())) {            return Response.status(Response.Status.BAD_REQUEST).entity("ID do objeto difere do ID da URL").build();        }        business.atualizar(saidaItem);        return Response.status(Response.Status.OK).entity(saidaItem).build();    }    @DELETE    @Path("{id}")    public Response excluir(@PathParam("id") Long id) {        business.excluir(id);        return Response.status(Response.Status.NO_CONTENT).build();    }}