package br.vandersoncamp.resources;import br.vandersoncamp.simples.business.SaidaBusiness;import br.vandersoncamp.simples.model.Saida;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("/vendas")@RequestScopedpublic class SaidaResource {    @Inject    private SaidaBusiness business;    @GET    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        Saida saida = business.buscar(id);        if (saida == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(saida).build();    }    @GET    @Path("all")    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar(            @QueryParam("filterField") String filterField,            @QueryParam("filterValue") String filterValue,            @QueryParam("order") String order) {        return Response.ok(business.pesquisar(filterField, filterValue, order)).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(Saida saida) {        business.criar(saida);        return Response.status(Response.Status.CREATED).entity(saida).build();    }    @PUT    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response atualizar(Saida saida, @PathParam("id") Long id) {        if (!id.equals(saida.getId())) {            return Response.status(Response.Status.BAD_REQUEST).entity("ID do objeto difere do ID da URL").build();        }        business.atualizar(saida);        return Response.status(Response.Status.OK).entity(saida).build();    }    @DELETE    @Path("{id}")    public Response excluir(@PathParam("id") Long id) {        business.excluir(id);        return Response.status(Response.Status.NO_CONTENT).build();    }}