package br.vandersoncamp.resources;import br.vandersoncamp.simples.business.VendaItemBusiness;import br.vandersoncamp.simples.model.VendaItem;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("/vendaitens")@RequestScopedpublic class VendaItemResource {    @Inject    private VendaItemBusiness business;    @GET    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        VendaItem vendaItem = business.buscar(id);        if (vendaItem == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(vendaItem).build();    }    @GET    @Path("all")    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar(            @QueryParam("filterField") String filterField,            @QueryParam("filterValue") String filterValue,            @QueryParam("order") String order) {        return Response.ok(business.pesquisar(filterField, filterValue, order)).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(VendaItem vendaItem) {        business.criar(vendaItem);        return Response.status(Response.Status.CREATED).entity(vendaItem).build();    }    @PUT    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response atualizar(VendaItem vendaItem, @PathParam("id") Long id) {        if (!id.equals(vendaItem.getId())) {            return Response.status(Response.Status.BAD_REQUEST).entity("ID do objeto difere do ID da URL").build();        }        business.atualizar(vendaItem);        return Response.status(Response.Status.OK).entity(vendaItem).build();    }    @DELETE    @Path("{id}")    public Response excluir(@PathParam("id") Long id) {        business.excluir(id);        return Response.status(Response.Status.NO_CONTENT).build();    }}